class ApiHandler {
    static async fetchLatestMovies() {
        try {
            const url = 'https://api.themoviedb.org/3/movie/latest?api_key=a26a1684ba14b7b49ebbd51f98eb95f7';
        
            const response = await fetch(url);
        
            const data = await response.json();
        
            return data.results;
        } catch (err) {
            console.error(err);
            throw err;
        }
    }
    
    static async fetchSearchedMovies(search) {
        try {
            const url = 'https://api.themoviedb.org/3/search/movie?api_key=a26a1684ba14b7b49ebbd51f98eb95f7&query=' + search;
            
            const response = await fetch(url);
            
            const data = await response.json();
            
            return data.results;
        } catch (err) {
            console.error(err);
            throw err;
        }
    }
}

module.exports = ApiHandler;